function binarySearch(array, target):
    low = 0
    high = length(array) - 1

    while low ≤ high:
        mid = floor((low + high) / 2)

        if array[mid] == target:
            return mid
        else if array[mid] < target:
            low = mid + 1
        else:
            high = mid - 1

    return -1  # target not found
